pickatime.js_code:
$("%selector%").pickatime({
     format: 'HH !h i m!in',
     formatLabel: '<b>H</b>:i',
     formatSubmit: 'HH:i',
     interval: 15
 });

pickatime-french.js_code:
$("%selector%").pickatime({
     format: 'HH !heures i m!inutes',
     formatLabel: '<b>H</b>:i',
     formatSubmit: 'HH:i',
     interval: 15
 });
